    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        ArrayList<ArrayList<Courses>> allCourses = new ArrayList<ArrayList<Courses>>();
        if (jComboBox2.getSelectedItem().toString().equals("No Preference"))
            allCourses.add((container.getAllCourse(jComboBox1.getSelectedItem().toString())));
        else
            allCourses.add((container.getAllCourse(jComboBox1.getSelectedItem().toString(), jComboBox2.getSelectedItem().toString() )));
        if (jComboBox8.getSelectedItem().toString().equals("No Preference"))
            allCourses.add((container.getAllCourse(jComboBox3.getSelectedItem().toString())));
        else
            allCourses.add((container.getAllCourse(jComboBox3.getSelectedItem().toString(), jComboBox8.getSelectedItem().toString() )));
        if (jComboBox9.getSelectedItem().toString().equals("No Preference"))
            allCourses.add((container.getAllCourse(jComboBox4.getSelectedItem().toString())));
        else
            allCourses.add((container.getAllCourse(jComboBox4.getSelectedItem().toString(), jComboBox9.getSelectedItem().toString() )));
        if (jComboBox10.getSelectedItem().toString().equals("No Preference"))
            allCourses.add((container.getAllCourse(jComboBox5.getSelectedItem().toString())));
        else
            allCourses.add((container.getAllCourse(jComboBox5.getSelectedItem().toString(), jComboBox10.getSelectedItem().toString() )));
        if (jComboBox11.getSelectedItem().toString().equals("No Preference"))
            allCourses.add((container.getAllCourse(jComboBox6.getSelectedItem().toString())));
        else
            allCourses.add((container.getAllCourse(jComboBox6.getSelectedItem().toString(), jComboBox11.getSelectedItem().toString() )));
        if (jComboBox12.getSelectedItem().toString().equals("No Preference"))
            allCourses.add((container.getAllCourse(jComboBox7.getSelectedItem().toString())));
        else
            allCourses.add((container.getAllCourse(jComboBox7.getSelectedItem().toString(), jComboBox12.getSelectedItem().toString() )));
        if (jComboBox14.getSelectedItem().toString().equals("No Preference"))
            allCourses.add((container.getAllCourse(jComboBox13.getSelectedItem().toString())));
        else
            allCourses.add((container.getAllCourse(jComboBox13.getSelectedItem().toString(), jComboBox14.getSelectedItem().toString() )));
        /*if (jComboBox16.getSelectedItem().toString().equals("No Preference"))
            allCourses.add((container.getAllCourse(jComboBox15.getSelectedItem().toString())));
        else
            allCourses.add((container.getAllCourse(jComboBox15.getSelectedItem().toString(), jComboBox16.getSelectedItem().toString() )));
        if (jComboBox18.getSelectedItem().toString().equals("No Preference"))
            allCourses.add((container.getAllCourse(jComboBox17.getSelectedItem().toString())));
        else
            allCourses.add((container.getAllCourse(jComboBox17.getSelectedItem().toString(), jComboBox18.getSelectedItem().toString() )));
        */
        allCourses = CourseContainer.combineAll(allCourses);
        allCourses = CourseContainer.checkAll(allCourses);
        
        for (ArrayList<Courses> course: allCourses){
            CourseContainer.printList(course);
        }
        System.out.println("DONE");
        if (allCourses.isEmpty())
            jLabel13.setText("There is no possible timetable. Please change your selections and try again");
        else{
            int i=1;
            try {
            FileWriter writer = new FileWriter("C:\\Users\\Hassan\\Desktop\\TimeTable.txt", true);
            for (ArrayList<Courses> course: allCourses){
                writer.write("TIMETABLE #" + i + "\r\n");
                writer.write(CourseContainer.printListToString(course));
                writer.write("\r\n");
                i++;
                
            }
            writer.write("\r\n");   // write new line
            writer.write("Good Bye!");
            writer.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
        }
        
    }//GEN-LAST:event_jButton1ActionPerformed

